import { useParams } from "react-router-dom";
import { getModule } from "../api";

export default function Module() {
  const { diplomaId, moduleId } = useParams();
  const module = getModule(diplomaId, moduleId);

  if (!module) {
    return <div className="hint">Select a module to view details.</div>;
  }

  return (
    <div>
      <h2>{module.name}</h2>

      <p className="module-desc">{module.details}</p>

      <div className="module-meta">
        <div>
          <strong>Year</strong>
          <span>{module.level}</span>
        </div>

        <div>
          <strong>Duration</strong>
          <span>{module.duration}</span>
        </div>
      </div>

      <div className="module-actions">
        <button className="btn outline">â˜† Save</button>
        <button className="btn">Register Interest</button>
      </div>
    </div>
  );
}
