import { useNavigate, useParams } from "react-router-dom";
import { getDiploma, getModule, isFav, toggleFav } from "../api";

export default function Module() {
  const navigate = useNavigate();
  const { diplomaId, moduleId } = useParams();

  const diploma = getDiploma(diplomaId);
  const module = getModule(diplomaId, moduleId);

  if (!diploma || !module) return <div className="hint">Module not found.</div>;

  const saved = isFav(diplomaId, moduleId);

  function onSave() {
    toggleFav(diplomaId, moduleId);
    navigate(0);
  }

  function onRegister() {
    sessionStorage.setItem(
      "rp_prefill",
      JSON.stringify({ diplomaId: diplomaId, moduleId: moduleId })
    );
    navigate("/register");
  }

  return (
    <div className="module-detail">
      <h3 className="module-h">
        {module.id} — {module.name}
      </h3>

      <p className="module-p">{module.details}</p>

      <div className="module-badges">
        <span className="badge">{module.level}</span>
        <span className="badge">{module.duration}</span>
      </div>

      <div className="module-actions">
        <button className={saved ? "btn star active" : "btn star"} type="button" onClick={onSave}>
          {saved ? "★ Saved" : "☆ Save"}
        </button>
        <button className="btn primary" type="button" onClick={onRegister}>
          Register Interest
        </button>
      </div>
    </div>
  );
}
