import { Link } from "react-router-dom";
import { getFavs, toggleFav, getDiploma } from "../api";
import { useState } from "react";

export default function Favourites() {
  const [favs, setFavs] = useState(getFavs());

  function handleRemove(key) {
    const [diplomaId, moduleId] = key.split(":");
    toggleFav(diplomaId, moduleId);
    setFavs(getFavs());
  }

  if (favs.length === 0) {
    return <div className="hint">No saved modules yet.</div>;
  }

  return (
    <div className="page">
      <h1 className="page-title">Favourites</h1>

      <div className="list">
        {favs.map((key) => {
          const [diplomaId, moduleId] = key.split(":");
          const diploma = getDiploma(diplomaId);
          const module = diploma?.modules.find((m) => m.id === moduleId);

          if (!module || !diploma) return null;

          return (
            <div key={key} className="item fav-row">
              <Link
                to={`/diplomas/${diplomaId}/${moduleId}`}
                className="fav-link"
              >
                <div className="fav-top">
                  <span className="pill soft">{module.id}</span>
                </div>

                <div className="fav-title">{module.name}</div>
                <div className="fav-desc">{diploma.name}</div>
              </Link>

              <button
                className="btn small danger"
                onClick={() => handleRemove(key)}
              >
                Remove
              </button>
            </div>
          );
        })}
      </div>
    </div>
  );
}
