import { NavLink, useNavigate } from "react-router-dom";
import { getFavs, setFavs, getDiploma, getModule } from "../api";

export default function Favourites() {
  const navigate = useNavigate();
  const favs = getFavs();

  function remove(key) {
    const next = favs.filter((x) => x !== key);
    setFavs(next);
    navigate(0);
  }

  const rows = favs
    .map((key) => {
      const parts = key.split(":");
      const dId = parts[0];
      const mId = parts[1];
      const d = getDiploma(dId);
      const m = getModule(dId, mId);
      if (!d || !m) return null;
      return { key, dId, mId, diplomaName: d.name, moduleName: m.name };
    })
    .filter(Boolean);

  return (
    <section className="favs">
      <h2 className="section-title">Favourites</h2>

      {rows.length === 0 ? (
        <div className="card empty-card">
          <p className="muted">No saved modules yet.</p>
          <NavLink className="btn primary" to="/diplomas">
            Browse Diplomas
          </NavLink>
        </div>
      ) : (
        <div className="list">
          {rows.map((r) => (
            <div key={r.key} className="fav-row">
              <NavLink className="fav-link" to={`/diplomas/${r.dId}/${r.mId}`}>
                <div className="fav-top">
                  <span className="pill">{r.dId}</span>
                  <span className="pill soft">{r.mId}</span>
                </div>
                <div className="fav-title">{r.moduleName}</div>
                <div className="fav-desc">{r.diplomaName}</div>
              </NavLink>
              <button className="btn small" type="button" onClick={() => remove(r.key)}>
                Remove
              </button>
            </div>
          ))}
        </div>
      )}
    </section>
  );
}
